const descriptions = [
  "Am watching You",
  "You cannot run",
  "Come to me",
  "The Internet is mine",
  "I love you"
];

const svgDataUrl = `data:image/svg+xml;base64,${btoa(faviconSvg)}`;

const getRandomDescription = () => {
  return descriptions[Math.floor(Math.random() * descriptions.length)];
};

const updateTitleTime = () => {
  const now = new Date();
  const hours = String(now.getHours()).padStart(2, '0');
  const minutes = String(now.getMinutes()).padStart(2, '0');
  const seconds = String(now.getSeconds()).padStart(2, '0');
  const timeStr = `${hours} : ${minutes} : ${seconds}`;
  
  document.title = timeStr;
  
  const titleSelectors = [
    'meta[name="title"]',
    'meta[property="og:title"]',
    'meta[name="twitter:title"]',
    'meta[itemprop="name"]',
    'meta[itemprop="headline"]',
    'meta[name="application-name"]',
    'meta[name="apple-mobile-web-app-title"]',
    'meta[property="og:site_name"]',
    'meta[name="parsely-title"]',
    'meta[name="citation_title"]',
    'meta[property="book:title"]',
    'meta[property="product:name"]',
    'meta[property="video:series"]',
    'meta[property="article:section"]',
    'meta[property="book:author"]',
    'meta[property="profile:first_name"]',
    'meta[property="profile:last_name"]'
  ];
  
  titleSelectors.forEach(selector => {
    const tag = document.querySelector(selector);
    if (tag) tag.content = timeStr;
  });
};

const updateFavicon = () => {
  const faviconSelectors = [
    'link[rel="icon"]',
    'link[rel="shortcut icon"]',
    'link[rel="apple-touch-icon"]',
    'link[rel="mask-icon"]',
    'meta[property="og:image"]',
    'meta[name="twitter:image"]',
    'meta[itemprop="image"]',
    'meta[name="msapplication-TileImage"]'
  ];
  
  faviconSelectors.forEach(selector => {
    const tag = document.querySelector(selector);
    if (tag) {
      if (tag.hasAttribute('href')) {
        tag.href = svgDataUrl;
      }
      if (tag.hasAttribute('content')) {
        tag.content = svgDataUrl;
      }
    }
  });
  
  let faviconLink = document.querySelector('link[rel="icon"]');
  if (!faviconLink) {
    faviconLink = document.createElement('link');
    faviconLink.rel = 'icon';
    faviconLink.type = 'image/svg+xml';
    document.head.appendChild(faviconLink);
  }
  faviconLink.href = svgDataUrl;
};

const updateDescription = () => {
  const randomDesc = getRandomDescription();
  
  const descSelectors = [
    'meta[name="description"]',
    'meta[property="og:description"]',
    'meta[name="twitter:description"]',
    'meta[itemprop="description"]'
  ];
  
  descSelectors.forEach(selector => {
    const tag = document.querySelector(selector);
    if (tag) tag.content = randomDesc;
  });
};

const init = () => {
  updateTitleTime();
  updateFavicon();
  updateDescription();
  
  setInterval(updateTitleTime, 1000);
  setInterval(updateDescription, 5000);
};

if (document.readyState === 'loading') {
  document.addEventListener('DOMContentLoaded', init);
} else {
  init();
}